mapper.select:

select
,

from user

  
    username = #{user.username,jdbcType=CHAR}
  
  
    and userid = #{user.userid,jdbcType=CHAR}
  
  
    and phone_number = #{user.phoneNumber,jdbcType=BIGINT}
  
  
    and mailbox = #{user.mailbox,jdbcType=CHAR}
  
  
    and password = #{user.password,jdbcType=CHAR}
  
  
    and nickname = #{user.nickname,jdbcType=CHAR}
  
  
    and sex = #{user.sex,jdbcType=BIT}
  
  
    and birthday = #{user.birthday,jdbcType=DATE}
  
  
    and signature = #{user.signature,jdbcType=CHAR}
  
  
    and currency = #{user.currency,jdbcType=BIGINT}
  
  
    and is_member = #{user.isMember,jdbcType=BIT}
  
  
    and is_online = #{user.isOnline,jdbcType=BIT}
  
  
    and is_borbid_login = #{user.isBorbidLogin,jdbcType=BIT}
  
  
    and last_login_time = #{user.lastLoginTime,jdbcType=TIMESTAMP}
  
  
    and last_pass_time = #{user.lastPassTime,jdbcType=TIMESTAMP}
  
  
    and registration_time like concat(#{user.registrationTime,jdbcType=TIMESTAMP},'%')
  
  
    and real_name = #{user.realName,jdbcType=CHAR}
  
  
    and document_type = #{user.documentType,jdbcType=CHAR}
  
  
    and document_number = #{user.documentNumber,jdbcType=CHAR}
  
  
    and head_portrait = #{user.headPortrait,jdbcType=LONGVARBINARY}
  
  
    and document_by_hand = #{user.documentByHand,jdbcType=LONGVARBINARY}
  
  
    and document_by_front = #{user.documentByFront,jdbcType=LONGVARBINARY}
  
  
    and document_by_back = #{user.documentByBack,jdbcType=LONGVARBINARY}
  


  limit #{pageLimit},#{limit}

select count(*) from user

  
    username = #{username,jdbcType=CHAR}
  
  
    and userid = #{userid,jdbcType=CHAR}
  
  
