    and mailbox = #{mailbox,jdbcType=CHAR}
  
  
    and password = #{password,jdbcType=CHAR}
  
  
    and nickname = #{nickname,jdbcType=CHAR}
  
  
    and sex = #{sex,jdbcType=BIT}
  
  
    and birthday = #{birthday,jdbcType=DATE}
  
  
    and signature = #{signature,jdbcType=CHAR}
  
  
    and currency = #{currency,jdbcType=BIGINT}
  
  
    and is_member = #{isMember,jdbcType=BIT}
  
  
    and is_online = #{isOnline,jdbcType=BIT}
  
  
    and is_borbid_login = #{isBorbidLogin,jdbcType=BIT}
  
  
    and last_login_time = #{lastLoginTime,jdbcType=TIMESTAMP}
  
  
    and last_pass_time = #{lastPassTime,jdbcType=TIMESTAMP}
  
  
    and registration_time like concat(#{registrationTime,jdbcType=TIMESTAMP},'%')
  
  
    and real_name = #{realName,jdbcType=CHAR}
  
  
    and document_type = #{documentType,jdbcType=CHAR}
  
  
    and document_number = #{documentNumber,jdbcType=CHAR}
  
  
    and head_portrait = #{headPortrait,jdbcType=LONGVARBINARY}
  
  
